import React, { useState } from 'react'
import personas from '../personas';

const FiltroApp = ({ setPersonas }) => {

    const [inputValue, setInputValue] = useState('')

    const findPersonaByNacionalidad = (inputValue) => personas.find((persona) => persona.nacionalidad === inputValue);    

    const handleSubmit = (e) => {
        e.preventDefault();
        if(inputValue.trim().length > 1){ //trim elimina los espacios en un string
            setPersonas(findPersonaByNacionalidad)
            setInputValue(() => '') // para que queda vacio luego del enter
        }
    }

    return (
        <form onSubmit={handleSubmit}>
            <input></input>
        </form>
    )
}

export default FiltroApp
